    return(
        <>
        <div>
            <h1>Transactions</h1>
            <br/>
            <NavLink to="/create">New Transaction</NavLink><br/>
            {error && <p style={{color: 'red'}}> {error} </p>}
            <br/>
            <Table  striped bordered hover variant="dark" width={1000}>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>AMOUNT</th>
                        <th>SENT TO</th>
                        <th>REFERENCE</th>
                        <th>DATE</th>
                        <th>STATUS</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>10245GFDGHFG</td>
                        <td>120000.00</td>
                        <td>J.P Warner</td>
                        <td>Z Smith</td>
                        <td>18/10/2024</td>
                        <td>Pending</td>
                        <td>
                            {/* <NavLink to={`/transaction/${transactions.Id}`}>Verify</NavLink> */}
