import React, {useState} from "react";

function Login()
{
    //Setting state/declaration
    const [username, setUsername] = useState('');
    const [acountNumber, setAcountNumber] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    //Setting function to not use default get method on form
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
    }

    return(
        <>
        <h1>Login</h1>
        <br/>

        <div>
            <a className="customerLoginTab" href="/">Customer </a> <a className="staffLoginTab" href="/">Staff</a>
        </div>
        <br/>

        <form onSubmit={handleSubmit} className="customerLoginForm" >
            <label htmlFor='cUsername'>Username</label><br/>
            <input type="text" id='cUsername' name="cUsername" value={username} onChange={(e) => setUsername(e.target.value)} autoComplete="true"/><br/>
            
            <label htmlFor='cAccNumber'>Account Number</label><br/>
            <input type="text" id='cAccNumber' name="cAccNumber" value={acountNumber} onChange={(e) => setAcountNumber(e.target.value)}/><br/>

            <label htmlFor='cPassword'>Password</label><br/>
            <input type="password" id='cPassword' name="cPassword" value={password} onChange={(e) => setPassword(e.target.value)}/><br/>
            <br/>

            {error && <p style={{color: 'red'}}> {error} </p>}

            <button type="submit">Login</button>
        </form>
        
        
        <form onSubmit={handleSubmit} className="staffLoginForm" hidden>
            <label htmlFor='sUsername'>Username</label><br/>
            <input type="text" id='sUsername' name="sUsername" value={username} onChange={(e) => setUsername(e.target.value)}/><br/>

            <label htmlFor='sPassword'>Password</label><br/>
            <input type="password" id='sPassword' name="sPassword" value={password} onChange={(e) => setPassword(e.target.value)}/><br/>
            <br/>

            {error && <p style={{color: 'red'}}> {error} </p>}

            <button type="submit">Login</button>
        </form>
        </>
    );
}

export default Login;
